$('.snippet').prepend('<button class="btn btn-default btn-sm" data-clipboard-snippet><span class="glyphicon glyphicon-copy"></span> Copy</button>');
$('.snippet .btn').click(function () {
    var clipboardSnippets = new Clipboard('.btn', {
        target: function (trigger) {
            return trigger.nextElementSibling;
        }
    });

})